import styled, { createGlobalStyle } from 'styled-components' // 组件样式 
// css in js js 代码里写 css
// jsx  html in js ts 配置 babel preset-react 
// 定义样式组件 首字母大写
// 做无状态 但界面状态丰富  style分配
// 好处：作用于当前组件 不影响其他的地方
// react native  flutter unionapp

const Wapper = styled.p`
  color: pink;
`
// 样式组件 全局 
const GlobalStyle = createGlobalStyle`
  html {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  }
`

export default function IndexPage() {// 向外输出函数式组件
    return (
        <div>Next , ssr in react!
            <GlobalStyle />
            {/* <Wapper>
                Hello World
            </Wapper> */}
            <Stats url="https://covid19.mathdro.id/api">
            </Stats>
        </div>
    )
}
